import React, {useState} from 'react';
export default function Login({onLogin}){
  const [username,setUsername]=useState('');
  const [password,setPassword]=useState('');
  const submit = (e)=>{
    e.preventDefault();
    if(username.trim()) onLogin(username.trim());
    else alert('Please enter username');
  }
  return (
    <div style={{maxWidth:420, margin:'30px auto'}} className='card'>
      <h2 className='center'>Sign in to BusBooker</h2>
      <form onSubmit={submit} style={{padding:16}}>
        <div className='form-row' style={{flexDirection:'column',alignItems:'stretch'}}>
          <label>Username</label>
          <input className='input' value={username} onChange={e=>setUsername(e.target.value)} placeholder='your name' />
        </div>
        <div className='form-row' style={{flexDirection:'column',alignItems:'stretch', marginTop:8}}>
          <label>Password</label>
          <input type='password' className='input' value={password} onChange={e=>setPassword(e.target.value)} placeholder='password' />
        </div>
        <div style={{display:'flex',gap:8,justifyContent:'space-between',alignItems:'center', marginTop:12}}>
          <button className='btn' type='submit'>Login</button>
          <div className='small'>Demo login â€” any username works</div>
        </div>
      </form>
    </div>
  )
}
